import React, { Component } from 'react'

export default class TodoForm extends Component {

    constructor(props){
        super(props)

        this.nameInput = React.createRef();
        this.descriptionInput = React.createRef();
        this.statusInput = React.createRef();

        this.state = {
            name: '',
            description: '',
            status:'',
            id: '',
        }
    }

    loadTodo = (todo) => {
        this.setState({
                    name: todo.name,
                    description: todo.description,
                    status: todo.status
                })
    }

    async saveTodo ()  {
        const {name,description,status }= this.state;

     fetch('http://localhost:4000/add', {
            method: 'post',
            headers: {'Content-Type': 'application/json',
            'Access-Control-Allow-Origin': '*'},

            body: JSON.stringify({
                name:name,
                description:description,
                status:status
            })
          })
          .then(response => response.json())
          .then(todo => {
              if(todo){

                // this.props.addTodo({
                //     name:name,
                //     description:description,
                //     status:status
                // });
                this.props.addTodo({
                    name: todo.name,
                    description: todo.description,
                    status: todo.status
                })            
              }      
              else{
               return;
              }
          }).catch(err => console.log(err))

    }

    onNameChange = (event) => {
        event.preventDefault();
        this.setState({name: event.target.value});
      }
      onStatusChange = (event) => {
        event.preventDefault();
        this.setState({status: event.target.value});
      }

      onDescriptionChange = (event) => {
        event.preventDefault();
        this.setState({description: event.target.value});
      }

    handleSubmit = e => {
        e.preventDefault();
        this.saveTodo();
         console.log(todo)
        this.setState({name:'',description:''})
      };


    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit} >
                    {/* <label>Todo Name</label>
                    <input type="text" ref={this.nameInput}/>
                    <label>Todo Description</label>
                    <input type="text" ref={this.descriptionInput} />
                    {/* <label>Todo Name</label>
                    <input type="text" ref={this.descriptionInput}/> */}

                    <label>Todo Name</label>
                    <input type="text" value={this.state.name} onChange={this.onNameChange}/>
                    <label>Todo Description</label>
                    <input type="text" value={this.state.description} onChange={this.onDescriptionChange} />
                    <button onClick={this.handleSubmit}>create todo</button>
                </form>           
            </div>
        )
    }
}
