import React, { Component } from 'react'
import TodoForm from '../todo-form/TodoForm';
import Todo from '../todo/Todo';

export default class TodoList extends Component {

    constructor(props){
        super(props)
        this.state = {
            todoList: []
        }
    }

     uuidv4() {
        return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
          var r = Math.random() * 16 | 0, v = c == 'x' ? r : (r & 0x3 | 0x8);
          return v.toString(16);
        });
      }

    addTodo = todo => {
        this.setState(state => ({
            todoList: [todo, ...state.todoList]
        }));
      };

    deleteTodo = (todo) => {

       const v = this.state.todoList.filter(todo1 => todo1.name !== todo.name);
       this.setState({todoList: v});
        console.log(this.state.todoList);

    }


    render() {
        const{todoList}=this.state;
        console.log(todoList);
        return (
            <div>
                <TodoForm addTodo={this.addTodo}/>
                {
                     todoList.map(todo => {
                        const {name,description}=todo;
                     return   <Todo key={Math.random + 1} name={name} description={description} deleteTodo={this.deleteTodo} />
                     })
                }
            </div>
        )
    }
}
