import React, { Component } from 'react'
import './todo.styles.css'

export default class Todo extends Component {
   // const status = statusClicked === false ? 'IN PROGRESS' : "COMPLETED"
            constructor(props){
                super(props);
                this.state = {
                    statusClicked: false
                }
            }

            onStatusChange = () =>{
                this.setState({statusClicked: !this.state.statusClicked}, () => console.log(this.state.statusClicked))
            }

    render() {
        const {name,description } = this.props;
        const status = this.state.statusClicked === false ? 'IN PROGRESS' : "COMPLETED"
        return (
            <div className="todo">
            <h1>{name}</h1>
            <p>{description}</p>
            <div className="class">
                <button onClick={() =>this.props.deleteTodo({name})}>-</button>
                <button onClick={() =>this.onStatusChange()}>{status}</button>
            </div>
        </div>
        )
    }
}

