import React, { Component } from 'react'
import TodoForm from '../todo-form/TodoForm';
import Todo from '../todo/Todo';

export default class TodoList extends Component {

    constructor(props){
        super(props)
        this.state = {
            todoList: [],
            statusClicked: false
        }
    }

    onStatusChange = () =>{
        this.setState({statusClicked: !this.state.statusClicked}, () => console.log(this.state.statusClicked))
    }

    addTodo = todo => {
        this.setState(state => ({
            todoList: [todo, ...state.todoList]
        }));
      };

    deleteTodo = (todo) => {
       const v = this.state.todoList.filter(todo1 => todo1.name !== todo.name);
       this.setState({todoList: v});

    }

    render() {
        const{todoList,statusClicked}=this.state;
        return (
            <div>
                <TodoForm addTodo={this.addTodo}/>
                {
                     todoList.map((todo) => {
                        const {name,description,id}=todo;
                     return   <Todo key={id}
                                 name={name} 
                                 description={description} 
                                 deleteTodo={this.deleteTodo} 
                                 statusClicked={statusClicked}/>
                     })
                }
            </div>
        )
    }
}
